mapper.select:
SELECT s.id, s.name, s.image_path, s.is_active, s.is_open, s.max_delivery_fee, s.min_delivery_fee, s.min_amount, GROUP_CONCAT(DISTINCT sc.category) AS categories FROM stores s LEFT JOIN store_category sc ON sc.store_id = s.id LEFT JOIN menus m ON m.store_id = s.id
AND (s.name LIKE '%${req.searchText}%' OR m.name LIKE '%${req.searchText}%')
AND sc.category = #{categoryCode}
GROUP BY s.id ORDER BY s.id DESC LIMIT #{req.size} OFFSET #{req.offset}

SELECT id
FROM stores
WHERE user_id = #{userId}

mapper.update:
UPDATE stores s
JOIN users u ON u.id = s.user_id

    s.category = #{category}
    , s.comment = #{comment}

    , s.business_number = #{businessNumber}


    , s.license_path = #{licensePath}

    , s.postcode = #{postcode}
    , s.address = #{address}
    , s.address_detail = #{addressDetail}
    , s.tel = #{tel}
    , s.owner_name = #{ownerName}

        , s.image_path = #{imagePath}

    , u.phone = #{phone}
    , u.email = #{email}

WHERE s.id = #{storeId}
AND user_id = #{userId}

UPDATE stores
SET is_active = IF(is_active = 1, 0, 1)
WHERE id = #{storeId}
AND user_id = #{userId}
